00795     void updateCentroid()
00796     {
00797         NeedCentUpdate = false;
00798 
00799         const int BatchCount = ( 1 << IntOverBits ) - 1;
00800         ptype* const cp = CentParams;
00801         const double cm = 1.0 / PopSize;
00802         ptype* const tp = TmpParams;
00803         int i;
00804         int j;
00805 
00806         if( PopSize <= BatchCount )
00807         {
00808             copyParams( tp, PopParams[ 0 ]);
00809 
00810             for( j = 1; j < PopSize; j++ )
00811             {
00812                 const ptype* const p = PopParams[ j ];
00813 
00814                 for( i = 0; i < ParamCount; i++ )
00815                 {
00816                     tp[ i ] += p[ i ];
00817                 }
00818             }
00819 
00820             for( i = 0; i < ParamCount; i++ )
00821             {
00822                 cp[ i ] = (ptype) ( tp[ i ] * cm );
00823             }
00824         }
00825         else
00826         {
00827             // Batched centroid calculation, for more precision and no integer
00828             // overflows.
00829 
00830             int pl = PopSize;
00831             j = 0;
00832             bool DoCopy = true;
00833 
00834             while( pl > 0 )
00835             {
00836                 int c = ( pl > BatchCount ? BatchCount : pl );
00837                 pl -= c;
00838                 c--;
00839 
00840                 copyParams( tp, PopParams[ j ]);
00841 
00842                 while( c > 0 )
00843                 {
00844                     j++;
00845                     const ptype* const p = PopParams[ j ];
00846 
00847                     for( i = 0; i < ParamCount; i++ )
00848                     {
00849                         tp[ i ] += p[ i ];
00850                     }
00851 
00852                     c--;
00853                 }
00854 
00855                 if( DoCopy )
00856                 {
00857                     DoCopy = false;
00858 
00859                     for( i = 0; i < ParamCount; i++ )
00860                     {
00861                         cp[ i ] = (ptype) ( tp[ i ] * cm );
00862                     }
00863                 }
00864                 else
00865                 {
00866                     for( i = 0; i < ParamCount; i++ )
00867                     {
00868                         cp[ i ] += (ptype) ( tp[ i ] * cm );
00869                     }
00870                 }
00871             }
00872         }
