01137                 }
01138             }
01139             else
01140             {
01141                 copyParams( rp, UpdParams );
01142                 NeedCentUpdate = true;
01143             }
01144         }
01145         else
01146         {
01147             NeedCentUpdate = true;
01148         }
01149 
01150         return( p );
01151     }
01152 
01153 protected:
01154     static const int IntOverBits = ( sizeof( ptype ) > 4 ? 5 : 3 ); 
01157     static const int IntMantBits = sizeof( ptype ) * 8 - 1 - IntOverBits; 
01161     static const int64_t IntMantMult = 1LL << IntMantBits; 
01163     static const int64_t IntMantMultM = -IntMantMult; 
01165     static const int64_t IntMantMult2 = ( IntMantMult << 1 ); 
01167     static const int64_t IntMantMask = IntMantMult - 1; 
