01255 
01256     static ptype wrapParam( CBiteRnd& rnd, const ptype v )
01257     {
01258         if( (ptype) 0.25 == 0 )
01259         {
01260             if( v < 0 )
01261             {
01262                 if( v > IntMantMultM )
01263                 {
01264                     return( (ptype) ( rnd.get() * -v ));
01265                 }
01266 
01267                 return( (ptype) ( rnd.getRaw() & IntMantMask ));
01268             }
01269 
01270             if( v > IntMantMult )
01271             {
01272                 if( v < IntMantMult2 )
01273                 {
01274                     return( (ptype) ( IntMantMult -
01275                         rnd.get() * ( v - IntMantMult )));
01276                 }
01277 
01278                 return( (ptype) ( rnd.getRaw() & IntMantMask ));
01279             }
01280 
01281             return( v );
01282         }
01283         else
01284         {
01285             if( v < 0.0 )
01286             {
01287                 if( v > -1.0 )
01288                 {
01289                     return( (ptype) ( rnd.get() * -v ));
01290                 }
01291 
01292                 return( (ptype) rnd.get() );
01293             }
01294 
01295             if( v > 1.0 )
01296             {
01297                 if( v < 2.0 )
01298                 {
01299                     return( (ptype) ( 1.0 - rnd.get() * ( v - 1.0 )));
01300                 }
01301 
01302                 return( (ptype) rnd.get() );
01303             }
01304 
01305             return( v );
01306         }
01307     }
01308 
01317 
01318     static ptype getGaussianInt( CBiteRnd& rnd, const double sd,
01319         const ptype meanInt )
01320     {
01321         while( true )
01322         {
01323             const double r = rnd.getGaussian() * sd;
01324 
01325             if( r > -8.0 && r < 8.0 )
01326             {
01327                 return( (ptype) ( r * IntMantMult + meanInt ));
01328             }
01329         }
01330     }
01331 
01338 
01339     static double calcLP1Coeff( const double Count )
01340     {
01341         const double theta = 2.8 / Count;
01342         const double costheta2 = 2.0 - cos( theta );
01343 
01344         return( 1.0 - ( costheta2 - sqrt( costheta2 * costheta2 - 1.0 )));
01345     }
01346 };
