01416 
01417     int getMinDistParPop( const double Cost, const ptype* const Params ) const
01418     {
01419         double s[ MaxParPopCount ];
01420         int i;
01421 
01422         if( ParPopCount == 4 )
01423         {
01424             const ptype* const c0 = ParPops[ 0 ] -> getCentroid();
01425             const ptype* const c1 = ParPops[ 1 ] -> getCentroid();
01426             const ptype* const c2 = ParPops[ 2 ] -> getCentroid();
01427             const ptype* const c3 = ParPops[ 3 ] -> getCentroid();
01428             double s0 = 0.0;
01429             double s1 = 0.0;
01430             double s2 = 0.0;
01431             double s3 = 0.0;
01432 
01433             for( i = 0; i < ParamCount; i++ )
01434             {
01435                 const ptype v = -Params[ i ];
01436                 const double d0 = (double) ( v + c0[ i ]);
01437                 const double d1 = (double) ( v + c1[ i ]);
01438                 s0 += d0 * d0;
01439                 s1 += d1 * d1;
01440 
01441                 const double d2 = (double) ( v + c2[ i ]);
01442                 const double d3 = (double) ( v + c3[ i ]);
01443                 s2 += d2 * d2;
01444                 s3 += d3 * d3;
01445             }
01446 
01447             s[ 0 ] = s0;
01448             s[ 1 ] = s1;
01449             s[ 2 ] = s2;
01450             s[ 3 ] = s3;
01451         }
01452         else
01453         if( ParPopCount == 3 )
01454         {
01455             const ptype* const c0 = ParPops[ 0 ] -> getCentroid();
01456             const ptype* const c1 = ParPops[ 1 ] -> getCentroid();
01457             const ptype* const c2 = ParPops[ 2 ] -> getCentroid();
01458             double s0 = 0.0;
01459             double s1 = 0.0;
01460             double s2 = 0.0;
01461 
01462             for( i = 0; i < ParamCount; i++ )
01463             {
01464                 const ptype v = -Params[ i ];
01465                 const double d0 = (double) ( v + c0[ i ]);
01466                 const double d1 = (double) ( v + c1[ i ]);
01467                 s0 += d0 * d0;
01468                 s1 += d1 * d1;
01469 
01470                 const double d2 = (double) ( v + c2[ i ]);
01471                 s2 += d2 * d2;
01472             }
01473 
01474             s[ 0 ] = s0;
01475             s[ 1 ] = s1;
01476             s[ 2 ] = s2;
01477         }
01478         else
01479         if( ParPopCount == 2 )
01480         {
01481             const ptype* const c0 = ParPops[ 0 ] -> getCentroid();
01482             const ptype* const c1 = ParPops[ 1 ] -> getCentroid();
01483             double s0 = 0.0;
01484             double s1 = 0.0;
01485 
01486             for( i = 0; i < ParamCount; i++ )
01487             {
01488                 const ptype v = -Params[ i ];
01489                 const double d0 = (double) ( v + c0[ i ]);
01490                 const double d1 = (double) ( v + c1[ i ]);
01491                 s0 += d0 * d0;
01492                 s1 += d1 * d1;
01493             }
01494 
01495             s[ 0 ] = s0;
01496             s[ 1 ] = s1;
01497         }
01498 
01499         int pp = 0;
01500         double d = s[ pp ];
01501 
01502         for( i = 1; i < ParPopCount; i++ )
01503         {
01504             if( s[ i ] <= d )
01505             {
01506                 pp = i;
01507                 d = s[ i ];
01508             }
01509         }
01510 
01511         return( pp );
01512     }
01513 };
