01743 
01744     void updateDiffValues()
01745     {
01746         int i;
01747 
01748         if( (ptype) 0.25 == 0 )
01749         {
01750             for( i = 0; i < ParamCount; i++ )
01751             {
01752                 const double d = MaxValues[ i ] - MinValues[ i ];
01753 
01754                 DiffValues[ i ] = d / IntMantMult;
01755                 DiffValuesI[ i ] = IntMantMult / d;
01756             }
01757         }
01758         else
01759         {
01760             for( i = 0; i < ParamCount; i++ )
01761             {
01762                 DiffValues[ i ] = MaxValues[ i ] - MinValues[ i ];
01763                 DiffValuesI[ i ] = 1.0 / DiffValues[ i ];
01764             }
01765         }
01766     }
