01778 
01779     void updateBestCost( const double UpdCost, const double* const UpdValues,
01780         const int p = -1 )
01781     {
01782         if( UpdCost != UpdCost ) // Check for NaN.
01783         {
01784             return;
01785         }
01786 
01787         if( p == 0 || ( p < 0 && UpdCost <= BestCost ))
01788         {
01789             BestCost = UpdCost;
01790 
01791             copyValues( BestValues, UpdValues );
01792         }
01793     }
