01818 
01819     double wrapParamReal( CBiteRnd& rnd, const double v, const int i ) const
01820     {
01821         const double minv = MinValues[ i ];
01822 
01823         if( v < minv )
01824         {
01825             const double dv = DiffValues[ i ];
01826 
01827             if( v > minv - dv )
01828             {
01829                 return( minv + rnd.get() * ( minv - v ));
01830             }
01831 
01832             return( minv + rnd.get() * dv );
01833         }
01834 
01835         const double maxv = MaxValues[ i ];
01836 
01837         if( v > maxv )
01838         {
01839             const double dv = DiffValues[ i ];
01840 
01841             if( v < maxv + dv )
01842             {
01843                 return( maxv - rnd.get() * ( v - maxv ));
01844             }
01845 
01846             return( maxv - rnd.get() * dv );
01847         }
01848 
01849         return( v );
01850     }
