00149         // Initialize solution vectors randomly, calculate objective function
00150         // values of these solutions.
00151 
00152         const double sd = 0.25 * InitRadius;
00153         int i;
00154         int j;
00155 
00156         if( InitParams == NULL )
00157         {
00158             for( j = 0; j < PopSize; j++ )
