00160                 ptype* const p = PopParams[ j ];
00161 
00162                 for( i = 0; i < ParamCount; i++ )
00163                 {
00164                     p[ i ] = wrapParam( rnd, getGaussianInt(
00165                         rnd, sd, IntMantMult >> 1 ));
