00221     int optimize( CBiteRnd& rnd, CBiteOpt* const PushOpt = NULL )
00222     {
00223         int i;
00224 
00225         if( DoInitEvals )
00226         {
00227             const ptype* const Params = getCurParams();
00228 
00229             for( i = 0; i < ParamCount; i++ )
00230             {
00231                 NewValues[ i ] = getRealValue( Params, i );
00232             }
00233 
00234             NewCost = optcost( NewValues );
00235             updateBestCost( NewCost, NewValues,
00236                 updatePop( NewCost, Params, false ));
00237 
00238             if( CurPopPos == PopSize )
00239             {
00240                 updateCentroid();
00241 
00242                 for( i = 0; i < ParPopCount; i++ )
00243                 {
00244                     ParPops[ i ] -> copy( *this );
00245                 }
00246 
00247                 DoInitEvals = false;
00248             }
00249 
00250             return( 0 );
00251         }
00252 
00253         DoEval = true;
00254 
00255         const int SelMethod = select( MethodSel, rnd );
00256 
00257         if( SelMethod == 0 )
00258         {
00259             generateSol2( rnd );
00260         }
00261         else
00262         if( SelMethod == 1 )
00263         {
00264             const int SelM1 = select( M1Sel, rnd );
00265 
00266             if( SelM1 == 0 )
00267             {
00268                 const int SelM1A = select( M1ASel, rnd );
00269 
00270                 if( SelM1A == 0 )
00271                 {
00272                     generateSol2b( rnd );
00273                 }
00274                 else
00275                 if( SelM1A == 1 )
00276                 {
00277                     generateSol2c( rnd );
00278                 }
00279                 else
00280                 {
00281                     generateSol2d( rnd );
00282                 }
00283             }
00284             else
00285             if( SelM1 == 1 )
00286             {
00287                 if( select( M1BSel, rnd ))
00288                 {
00289                     generateSol4( rnd );
00290                 }
00291                 else
00292                 {
00293                     generateSol5b( rnd );
00294                 }
00295             }
00296             else
00297             if( SelM1 == 2 )
00298             {
00299                 if( select( M1CSel, rnd ))
00300                 {
00301                     generateSol5( rnd );
00302                 }
00303                 else
00304                 {
00305                     generateSol10( rnd );
00306                 }
00307             }
00308             else
00309             {
00310                 generateSol6( rnd );
00311             }
00312         }
00313         else
00314         if( SelMethod == 2 )
00315         {
00316             if( select( M2Sel, rnd ))
00317             {
00318                 generateSol1( rnd );
00319             }
00320             else
00321             {
00322                 const int SelM2B = select( M2BSel, rnd );
00323 
00324                 if( SelM2B == 0 )
00325                 {
00326                     generateSol3( rnd );
00327                 }
00328                 else
00329                 if( SelM2B == 1 )
00330                 {
00331                     generateSol7( rnd );
00332                 }
00333                 else
00334                 if( SelM2B == 2 )
00335                 {
00336                     generateSol8( rnd );
00337                 }
00338                 else
00339                 {
00340                     generateSol9( rnd );
00341                 }
00342             }
00343         }
00344         else
00345         {
00346             generateSolPar( rnd );
00347         }
00348 
00349         if( DoEval )
00350         {
00351             // Evaluate objective function with new parameters, if the
00352             // solution was not provided by the parallel optimizer.
00353             // Wrap parameter values so that they stay in the [0; 1] range.
00354 
00355             for( i = 0; i < ParamCount; i++ )
00356             {
00357                 TmpParams[ i ] = wrapParam( rnd, TmpParams[ i ]);
00358                 NewValues[ i ] = getRealValue( TmpParams, i );
00359             }
00360 
00361             NewCost = optcost( NewValues );
00362         }
00363 
00364         const int p = updatePop( NewCost, TmpParams, true );
00365 
00366         if( p > CurPopSize1 )
00367         {
00368             // Upper bound cost constraint check failed, reject this solution.
00369 
00370             applySelsDecr( rnd );
00371 
00372             StallCount++;
00373 
00374             if( DoEval && CurPopSize < PopSize )
00375             {
00376                 if( select( PopChangeIncrSel, rnd ))
00377                 {
00378                     // Increase population size on fail.
00379 
00380                     incrCurPopSize();
00381                 }
00382             }
00383         }
00384         else
00385         {
00386             updateBestCost( NewCost, NewValues, p );
00387             applySelsIncr( rnd, 1.0 - p * CurPopSizeI );
00388 
00389             StallCount = 0;
00390 
00391             if( rnd.get() < ParamCountI )
00392             {
00393                 OldPop.updatePop( *getObjPtr( PopParams[ CurPopSize1 ]),
00394                     PopParams[ CurPopSize1 ], false );
00395             }
00396 
00397             if( PushOpt != NULL && PushOpt != this &&
00398                 !PushOpt -> DoInitEvals && p > 0 )
00399             {
00400                 PushOpt -> updatePop( NewCost, TmpParams, true );
00401                 PushOpt -> updateParPop( NewCost, TmpParams );
00402             }
00403 
00404             if( DoEval && CurPopSize > PopSize / 2 )
00405             {
00406                 if( select( PopChangeDecrSel, rnd ))
00407                 {
00408                     // Decrease population size on success.
00409 
00410                     decrCurPopSize();
00411                 }
00412             }
00413         }
00414 
00415         // "Diverging populations" technique.
00416 
00417         updateParPop( NewCost, TmpParams );
00418 
00419         return( StallCount );
00420     }
