00581     int getMinSolIndex( const int gi, CBiteRnd& rnd, const int ps )
00582     {
00583         static const double pp[ 4 ] = { 0.05, 0.125, 0.25, 0.5 };
00584         const double r = ps * rnd.getPow( ps *
00585             pp[ select( MinSolPwrSel[ gi ], rnd )]);
00586 
00587         static const double rm[ 4 ] = { 0.0, 0.125, 0.25, 0.5 };
00588 
00589         return( (int) ( r * rm[ select( MinSolMulSel[ gi ], rnd )]));
00590     }
