00598     void generateSol1( CBiteRnd& rnd )
00599     {
00600         ptype* const Params = TmpParams;
00601 
00602         const CBitePop& ParPop = selectParPop( 0, rnd );
00603 
00604         copyParams( Params, ParPop.getParamsOrdered(
00605             getMinSolIndex( 0, rnd, ParPop.getCurPopSize() )));
00606 
00607         // Select a single random parameter or all parameters for further
00608         // operations.
00609 
00610         int a;
00611         int b;
00612         bool DoAllp = false;
00613 
00614         if( rnd.get() < 1.8 * ParamCountI )
00615         {
00616             if( select( Gen1AllpSel, rnd ))
00617             {
00618                 DoAllp = true;
00619             }
00620         }
00621 
00622         if( DoAllp )
00623         {
00624             a = 0;
00625             b = ParamCount;
00626         }
00627         else
00628         {
00629             a = rnd.getInt( ParamCount );
00630             b = a + 1;
00631         }
00632 
00633         // Bitmask inversion operation, works as the main "driver" of
00634         // optimization process.
00635 
00636         const double r1 = rnd.get();
00637         const double r12 = r1 * r1;
00638         const int ims = (int) ( r12 * r12 * 48.0 );
00639         const ptype imask = (ptype) ( IntMantMask >> ims );
00640 
00641         const int im2s = rnd.getSqrInt( 96 );
00642         const ptype imask2 = (ptype) ( im2s > 63 ? 0 : IntMantMask >> im2s );
00643 
00644         const int si1 = (int) ( r1 * r12 * ParPop.getCurPopSize() );
00645         const ptype* const rp1 = ParPop.getParamsOrdered( si1 );
00646         int i;
00647 
00648         for( i = a; i < b; i++ )
00649         {
00650             Params[ i ] = (( Params[ i ] ^ imask ) +
00651                 ( rp1[ i ] ^ imask2 )) >> 1;
00652         }
00653 
00654         if( rnd.get() < 1.0 - ParamCountI )
00655         {
00656             const ptype* const rp2 = getParamsOrdered(
00657                 rnd.getSqrInt( CurPopSize ));
00658 
00659             if( rnd.get() < sqrt( ParamCountI ))
00660             {
00661                 if( select( Gen1MoveAsyncSel, rnd ))
00662                 {
00663                     a = 0;
00664                     b = ParamCount;
00665                 }
00666             }
00667 
00668             // Random move around a random previous solution vector.
00669 
00670             static const double SpanMults[ 4 ] = { 0.5, 1.5, 2.0, 2.5 };
00671 
00672             const double m = SpanMults[ select( Gen1MoveSpanSel, rnd )];
00673             const double m1 = rnd.getTPDF() * m;
00674             const double m2 = rnd.getTPDF() * m;
00675 
00676             for( i = a; i < b; i++ )
00677             {
00678                 Params[ i ] += (ptype) (( rp2[ i ] - Params[ i ]) * m1 );
00679                 Params[ i ] += (ptype) (( rp2[ i ] - Params[ i ]) * m2 );
00680             }
00681         }
00682     }
