00690     void generateSol2( CBiteRnd& rnd )
00691     {
00692         ptype* const Params = TmpParams;
00693 
00694         const int si1 = getMinSolIndex( 1, rnd, CurPopSize );
00695         const ptype* const rp1 = getParamsOrdered( si1 );
00696         const ptype* const rp3 = getParamsOrdered( CurPopSize1 - si1 );
00697 
00698         const int si2 = 1 + rnd.getInt( CurPopSize1 );
00699         const ptype* const rp2 = getParamsOrdered( si2 );
00700 
00701         const int si4 = rnd.getSqrInt( CurPopSize );
00702         const ptype* const rp4 = getParamsOrdered( si4 );
00703         const ptype* const rp5 = getParamsOrdered( CurPopSize1 - si4 );
00704 
00705         // The "step in the right direction" (Differential Evolution
00706         // "mutation") operation towards the best (minimal) and away from
00707         // the worst (maximal) parameter vector, plus a difference of two
00708         // random vectors.
00709 
00710         const int Mode = select( Gen2ModeSel, rnd );
00711         int i;
00712 
00713         if( Mode == 0 )
00714         {
00715             for( i = 0; i < ParamCount; i++ )
00716             {
00717                 Params[ i ] = rp1[ i ] + ((( rp2[ i ] - rp3[ i ]) +
00718                     ( rp4[ i ] - rp5[ i ])) >> 1 );
00719             }
00720         }
00721         else
00722         {
00723             const ptype* const rp1b = getParamsOrdered(
00724                 rnd.getSqrInt( CurPopSize ));
00725 
00726             for( i = 0; i < ParamCount; i++ )
00727             {
00728                 Params[ i ] = (( rp1[ i ] + rp1b[ i ]) +
00729                     ( rp2[ i ] - rp3[ i ]) + ( rp4[ i ] - rp5[ i ])) >> 1;
00730             }
00731         }
00732     }
