00738     void generateSol2b( CBiteRnd& rnd )
00739     {
00740         ptype* const Params = TmpParams;
00741 
00742         // rand/2/none DE-alike mutation.
00743 
00744         const int si1 = getMinSolIndex( 2, rnd, CurPopSize );
00745         const ptype* const rp1 = getParamsOrdered( si1 );
00746 
00747         const int si2 = rnd.getInt( CurPopSize );
00748         const ptype* const rp2 = getParamsOrdered( si2 );
00749         const ptype* const rp3 = getParamsOrdered( CurPopSize1 - si2 );
00750 
00751         const CBitePop& AltPop = selectAltPop( 0, rnd );
00752 
00753         const int si4 = rnd.getInt( CurPopSize );
00754         const ptype* const rp4 = AltPop.getParamsOrdered( si4 );
00755         const ptype* const rp5 = AltPop.getParamsOrdered( CurPopSize1 - si4 );
00756 
00757         const int Mode = select( Gen2bModeSel, rnd );
00758         int i;
00759 
00760         if( Mode == 0 )
00761         {
00762             for( i = 0; i < ParamCount; i++ )
00763             {
00764                 Params[ i ] = rp1[ i ] + (( rp2[ i ] - rp3[ i ]) +
00765                     ( rp4[ i ] - rp5[ i ]));
00766             }
00767         }
00768         else
00769         {
00770             const ptype* const rp1b = getParamsOrdered(
00771                 rnd.getSqrInt( CurPopSize ));
00772 
00773             for( i = 0; i < ParamCount; i++ )
00774             {
00775                 Params[ i ] = (( rp1[ i ] + rp1b[ i ]) >> 1 ) +
00776                     ( rp2[ i ] - rp3[ i ]) + ( rp4[ i ] - rp5[ i ]);
00777             }
00778         }
00779     }
