00786     void generateSol2c( CBiteRnd& rnd )
00787     {
00788         ptype* const Params = TmpParams;
00789         zeroParams( Params );
00790 
00791         const int si1 = rnd.getPowInt( 4.0, CurPopSize / 2 );
00792         const ptype* const rp1 = getParamsOrdered( si1 );
00793 
00794         const int PairCount = 3;
00795         const int pc = 1 + 2 * PairCount;
00796         int PopIdx[ pc ];
00797         PopIdx[ 0 ] = si1;
00798 
00799         int pp = 1;
00800         int i;
00801         int j;
00802 
00803         if( CurPopSize1 <= pc )
00804         {
00805             while( pp < pc )
00806             {
00807                 PopIdx[ pp ] = rnd.getInt( CurPopSize );
00808                 pp++;
00809             }
00810         }
00811         else
00812         {
00813             while( pp < pc )
00814             {
00815                 const int sii = rnd.getInt( CurPopSize );
00816 
00817                 for( j = 0; j < pp; j++ )
00818                 {
00819                     if( PopIdx[ j ] == sii )
00820                     {
00821                         break;
00822                     }
00823                 }
00824 
00825                 if( j == pp )
00826                 {
00827                     PopIdx[ pp ] = sii;
00828                     pp++;
00829                 }
00830             }
00831         }
00832 
00833         const ptype* const rp2 = getParamsOrdered( PopIdx[ 1 ]);
00834         const ptype* const rp3 = getParamsOrdered( PopIdx[ 2 ]);
00835         const ptype* const rp4 = getParamsOrdered( PopIdx[ 3 ]);
00836         const ptype* const rp5 = getParamsOrdered( PopIdx[ 4 ]);
00837         const ptype* const rp6 = getParamsOrdered( PopIdx[ 5 ]);
00838         const ptype* const rp7 = getParamsOrdered( PopIdx[ 6 ]);
00839 
00840         for( i = 0; i < ParamCount; i++ )
00841         {
00842             Params[ i ] = ( rp2[ i ] - rp3[ i ]) + ( rp4[ i ] - rp5[ i ]) +
00843                 ( rp6[ i ] - rp7[ i ]);
00844         }
00845 
00846         if( rnd.getBit() && rnd.getBit() )
00847         {
00848             const int k = rnd.getInt( ParamCount );
00849 
00850             // Produce sparsely-random bit-strings.
00851 
00852             const ptype v1 = (ptype) ( rnd.getRaw() & rnd.getRaw() &
00853                 rnd.getRaw() & rnd.getRaw() & rnd.getRaw() & IntMantMask );
00854 
00855             const ptype v2 = (ptype) ( rnd.getRaw() & rnd.getRaw() &
00856                 rnd.getRaw() & rnd.getRaw() & rnd.getRaw() & IntMantMask );
00857 
00858             Params[ k ] += v1 - v2; // Apply in TPDF manner.
00859         }
00860 
00861         const int Mode = select( Gen2cModeSel, rnd );
00862 
00863         if( Mode == 0 )
00864         {
00865             int si2 = si1 + rnd.getBit() * 2 - 1;
00866 
00867             if( si2 < 0 )
00868             {
00869                 si2 = 1;
00870             }
00871 
00872             const ptype* const rp1b = getParamsOrdered( si2 );
00873 
00874             for( i = 0; i < ParamCount; i++ )
00875             {
00876                 Params[ i ] = ( rp1[ i ] + rp1b[ i ] + Params[ i ]) >> 1;
00877             }
00878         }
00879         else
00880         {
00881             for( i = 0; i < ParamCount; i++ )
00882             {
00883                 Params[ i ] = rp1[ i ] + ( Params[ i ] >> 1 );
00884             }
00885         }
00886     }
00887 
