00893     void generateSol2d( CBiteRnd& rnd )
00894     {
00895         if( OldPop.getCurPopPos() < 3 )
00896         {
00897             generateSol2c( rnd );
00898             return;
00899         }
00900 
00901         ptype* const Params = TmpParams;
00902 
00903         const ptype* const rp1 = getParamsOrdered(
00904             rnd.getSqrInt( CurPopSize ));
00905 
00906         const ptype* const rp2 = getParamsOrdered(
00907             rnd.getInt( CurPopSize ));
00908 
00909         const ptype* const rp3 = OldPop.getParamsOrdered(
00910             rnd.getInt( OldPop.getCurPopPos() ));
00911 
00912         const int Mode = select( Gen2dModeSel, rnd );
00913         int i;
00914 
00915         if( Mode == 0 )
00916         {
00917             for( i = 0; i < ParamCount; i++ )
00918             {
00919                 Params[ i ] = rp1[ i ] + (( rp2[ i ] - rp3[ i ]) >> 1 );
00920             }
00921         }
00922         else
00923         {
00924             const ptype* const rp1b = getParamsOrdered(
00925                 rnd.getSqrInt( CurPopSize ));
00926 
00927             for( i = 0; i < ParamCount; i++ )
00928             {
00929                 Params[ i ] = (( rp1[ i ] + rp1b[ i ]) +
00930                     ( rp2[ i ] - rp3[ i ])) >> 1;
00931             }
00932         }
00933     }
