00941     void generateSol3( CBiteRnd& rnd )
00942     {
00943         ptype* const Params = TmpParams;
00944 
00945         const ptype* const rp1 = getParamsOrdered(
00946             getMinSolIndex( 3, rnd, CurPopSize ));
00947 
00948         const ptype* const rp2 = getParamsOrdered(
00949             rnd.getSqrInt( CurPopSize ));
00950 
00951         const int Mode = select( Gen3ModeSel, rnd );
00952         int i;
00953 
00954         if( Mode == 0 )
00955         {
00956             for( i = 0; i < ParamCount; i++ )
00957             {
00958                 Params[ i ] = rp1[ i ] + ( rp1[ i ] - rp2[ i ]);
00959             }
00960         }
00961         else
00962         {
00963             static const double CentProb[ 4 ] = { 0.0, 0.25, 0.5, 0.75 };
00964             const double p = CentProb[ Mode ];
00965 
00966             const ptype* const cp = getCentroid();
00967 
00968             for( i = 0; i < ParamCount; i++ )
00969             {
00970                 Params[ i ] = ( rnd.get() < p ? cp[ i ] :
00971                     rp1[ i ] + ( rp1[ i ] - rp2[ i ]));
00972             }
00973         }
00974     }
