00983     void generateSol4( CBiteRnd& rnd )
00984     {
00985         ptype* const Params = TmpParams;
00986 
00987         const CBitePop* UsePops[ 2 ];
00988         UsePops[ 0 ] = &selectAltPop( 1, rnd );
00989         UsePops[ 1 ] = &selectParPop( 1, rnd );
00990 
00991         int UseSize[ 2 ];
00992         UseSize[ 0 ] = CurPopSize;
00993         UseSize[ 1 ] = UsePops[ 1 ] -> getCurPopSize();
00994 
00995         const int km = 5 + ( select( Gen4MixFacSel, rnd ) << 1 );
00996 
00997         int p = rnd.getBit();
00998         const ptype* rp1 = UsePops[ p ] -> getParamsOrdered(
00999             rnd.getSqrInt( UseSize[ p ]));
01000 
01001         copyParams( Params, rp1 );
01002         int k;
01003 
01004         for( k = 1; k < km; k++ )
01005         {
01006             p = rnd.getBit();
01007             rp1 = UsePops[ p ] -> getParamsOrdered(
01008                 rnd.getSqrInt( UseSize[ p ]));
01009 
01010             int i;
01011 
01012             for( i = 0; i < ParamCount; i++ )
01013             {
01014                 Params[ i ] ^= rp1[ i ];
01015             }
01016         }
01017     }
