01029     void generateSol5( CBiteRnd& rnd )
01030     {
01031         ptype* const Params = TmpParams;
01032 
01033         const CBitePop& ParPop = selectParPop( 2, rnd );
01034 
01035         const int si1 = rnd.getSqrInt( ParPop.getCurPopSize() );
01036         const ptype* const CrossParams1 = ParPop.getParamsOrdered( si1 );
01037 
01038         const CBitePop& AltPop = selectAltPop( 2, rnd );
01039 
01040         const ptype* const CrossParams2 = AltPop.getParamsOrdered(
01041             rnd.getSqrInt( CurPopSize ));
01042 
01043         int i;
01044 
01045         for( i = 0; i < ParamCount; i++ )
01046         {
01047             // Produce a random bit-mixing mask.
01048 
01049             const ptype crpl = (ptype) ( rnd.getRaw() & IntMantMask );
01050 
01051             Params[ i ] = ( CrossParams1[ i ] & crpl ) |
01052                 ( CrossParams2[ i ] & ~crpl );
01053 
01054             // Randomize a single bit, with 50% probability.
01055 
01056             const int b = rnd.getInt( IntMantBits );
01057 
01058             Params[ i ] += ( (ptype) rnd.getBit() << b ) -
01059                 ( (ptype) rnd.getBit() << b );
01060         }
01061     }
