01068     void generateSol5b( CBiteRnd& rnd )
01069     {
01070         ptype* const Params = TmpParams;
01071         const ptype* CrossParams[ 4 ];
01072 
01073         const CBitePop& ParPop = selectParPop( 3, rnd );
01074 
01075         CrossParams[ 0 ] = ParPop.getParamsOrdered(
01076             rnd.getSqrInt( ParPop.getCurPopSize() ));
01077 
01078         const CBitePop& AltPop = selectAltPop( 3, rnd );
01079 
01080         if( rnd.getBit() )
01081         {
01082             CrossParams[ 1 ] = AltPop.getParamsOrdered(
01083                 CurPopSize1 - rnd.getSqrInt( CurPopSize ));
01084         }
01085         else
01086         {
01087             CrossParams[ 1 ] = AltPop.getParamsOrdered(
01088                 rnd.getSqrInt( CurPopSize ));
01089         }
01090 
01091         const int Mode = select( Gen5bModeSel, rnd );
01092         int i;
01093 
01094         if( Mode == 0 )
01095         {
01096             for( i = 0; i < ParamCount; i++ )
01097             {
01098                 Params[ i ] = CrossParams[ rnd.getBit() ][ i ];
01099             }
01100         }
01101         else
01102         {
01103             CrossParams[ 2 ] = ParPop.getParamsOrdered(
01104                 rnd.getSqrInt( ParPop.getCurPopSize() ));
01105 
01106             CrossParams[ 3 ] = AltPop.getParamsOrdered(
01107                 rnd.getSqrInt( CurPopSize ));
01108 
01109             for( i = 0; i < ParamCount; i++ )
01110             {
01111                 Params[ i ] = CrossParams[ rnd.getBit() << 1 |
01112                     rnd.getBit() ][ i ];
01113             }
01114         }
01115     }
