01126     void generateSol6( CBiteRnd& rnd )
01127     {
01128         ptype* const Params = TmpParams;
01129 
01130         const double r = rnd.getPow( 4.0 );
01131         const int si = (int) ( r * CurPopSize );
01132 
01133         double v[ 2 ];
01134         v[ 0 ] = getRealValue( getParamsOrdered( si ),
01135             rnd.getInt( ParamCount ));
01136 
01137         if( rnd.getBit() )
01138         {
01139             v[ 1 ] = getRealValue( getParamsOrdered( si ),
01140                 rnd.getInt( ParamCount ));
01141         }
01142         else
01143         {
01144             v[ 1 ] = v[ 0 ];
01145         }
01146 
01147         const double m = 1.0 - r * r;
01148         v[ 0 ] *= m; // Move towards real 0, useful for some functions.
01149         v[ 1 ] *= m;
01150 
01151         int i;
01152 
01153         for( i = 0; i < ParamCount; i++ )
01154         {
01155             Params[ i ] = (ptype) (( v[ rnd.getBit() ] - MinValues[ i ]) *
01156                 DiffValuesI[ i ]);
01157         }
01158     }
