01167     void generateSol7( CBiteRnd& rnd )
01168     {
01169         ptype* const Params = TmpParams;
01170 
01171         const bool UseOldPop = ( OldPop.getCurPopPos() > 2 );
01172         static const double p[ 4 ] = { 1.5, 1.75, 2.0, 2.25 };
01173         const double pwr = p[ select( Gen7PowFacSel, rnd )];
01174         int i;
01175 
01176         for( i = 0; i < ParamCount; i++ )
01177         {
01178             const double rv = rnd.getPow( pwr );
01179 
01180             if( UseOldPop && rnd.getBit() && rnd.getBit() )
01181             {
01182                 Params[ i ] = OldPop.getParamsOrdered(
01183                     (int) ( rv * OldPop.getCurPopPos() ))[ i ];
01184             }
01185             else
01186             {
01187                 Params[ i ] = getParamsOrdered(
01188                     (int) ( rv * CurPopSize ))[ i ];
01189             }
01190         }
01191     }
