01201     void generateSol8( CBiteRnd& rnd )
01202     {
01203         ptype* const Params = TmpParams;
01204 
01205         const int Mode = select( Gen8ModeSel, rnd );
01206         const int NumSols = 5 + select( Gen8NumSel, rnd );
01207         const ptype* rp[ 8 ];
01208 
01209         // Calculate centroid of a number of selected solutions.
01210 
01211         const ptype* rp0 = getParamsOrdered( rnd.getSqrInt( CurPopSize ));
01212         rp[ 0 ] = rp0;
01213         copyParams( Params, rp0 );
01214 
01215         int j;
01216         int i;
01217 
01218         for( j = 1; j < NumSols; j++ )
01219         {
01220             rp0 = getParamsOrdered( rnd.getSqrInt( CurPopSize ));
01221             rp[ j ] = rp0;
01222 
01223             for( i = 0; i < ParamCount; i++ )
01224             {
01225                 Params[ i ] += rp0[ i ];
01226             }
01227         }
01228 
01229         const double m = 1.0 / NumSols;
01230 
01231         for( i = 0; i < ParamCount; i++ )
01232         {
01233             NewValues[ i ] = Params[ i ] * m; // Centroid.
01234             Params[ i ] = (ptype) NewValues[ i ];
01235         }
01236 
01237         // Apply "move" operations in one of two modes.
01238 
01239         if( Mode == 0 )
01240         {
01241             static const double Spans[ 4 ] = { 1.5, 2.5, 3.5, 4.5 };
01242             const double gm = Spans[ select( Gen8SpanSel[ Mode ], rnd )] *
01243                 sqrt( m );
01244 
01245             for( j = 0; j < NumSols; j++ )
01246             {
01247                 const double r = rnd.getGaussian() * gm;
01248                 rp0 = rp[ j ];
01249 
01250                 for( i = 0; i < ParamCount; i++ )
01251                 {
01252                     Params[ i ] += (ptype) (( NewValues[ i ] - rp0[ i ]) * r );
01253                 }
01254             }
01255         }
01256         else
01257         {
01258             static const double Spans[ 4 ] = { 0.5, 1.5, 2.5, 3.5 };
01259             const double gm = Spans[ select( Gen8SpanSel[ Mode ], rnd )];
01260 
01261             for( j = 0; j < NumSols; j++ )
01262             {
01263                 const double r = rnd.getGaussian() * gm;
01264                 rp0 = rp[ j ];
01265 
01266                 for( i = 0; i < ParamCount; i++ )
01267                 {
01268                     Params[ i ] += (ptype) (( Params[ i ] - rp0[ i ]) * r );
01269                 }
01270             }
01271         }
01272     }
