01280     void generateSol9( CBiteRnd& rnd )
01281     {
01282         ptype* const Params = TmpParams;
01283 
01284         const int si1 = rnd.getInt( CurPopSize );
01285         const ptype* const rp1 = getParamsOrdered( si1 );
01286 
01287         const int si2 = rnd.getSqrInt( CurPopSize );
01288         const ptype* const rp2 = getParamsOrdered( CurPopSize1 - si2 );
01289         int i;
01290 
01291         // Such overall sign inversion seems unuseful, but has benefits in
01292         // practice.
01293 
01294         if( rnd.getBit() )
01295         {
01296             for( i = 0; i < ParamCount; i++ )
01297             {
01298                 Params[ i ] = rp1[ i ] - (( rp2[ i ] - rp1[ i ]) >> 1 ) *
01299                     ( 1 - 2 * rnd.getBit() );
01300             }
01301         }
01302         else
01303         {
01304             for( i = 0; i < ParamCount; i++ )
01305             {
01306                 Params[ i ] = rp1[ i ] + (( rp2[ i ] - rp1[ i ]) >> 1 ) *
01307                     ( 1 - 2 * rnd.getBit() );
01308             }
01309         }
01310     }
01311 
01315 
01316     void generateSol10( CBiteRnd& rnd )
01317     {
01318         ptype* const Params = TmpParams;
01319 
01320         const int si1 = rnd.getSqrInt( CurPopSize );
01321         const ptype* const rp1 = getParamsOrdered( si1 );
01322 
01323         const int si2 = rnd.getSqrInt( CurPopSize );
01324         const ptype* const rp2 = getParamsOrdered( CurPopSize1 - si2 );
01325         int i;
01326 
01327         // Calculate centroid.
01328 
01329         for( i = 0; i < ParamCount; i++ )
01330         {
01331             Params[ i ] = ( rp1[ i ] + rp2[ i ]) >> 1;
01332         }
01333 
01334         // Calculate radius.
01335 
01336         double Radius = 0.0;
01337 
01338         for( i = 0; i < ParamCount; i++ )
01339         {
01340             const ptype v1 = rp1[ i ] - Params[ i ];
01341             const ptype v2 = rp2[ i ] - Params[ i ];
01342             Radius += (double) v1 * v1 + 0.45 * v2 * v2;
01343         }
01344 
01345         // Select a point on a hyper-spheroid.
01346 
01347         double s2 = 1e-300;
01348 
01349         for( i = 0; i < ParamCount; i++ )
01350         {
01351             NewValues[ i ] = rnd.get() - 0.5;
01352             s2 += NewValues[ i ] * NewValues[ i ];
01353         }
01354 
01355         // Add hyper-spheroid-based offset to the centroid.
01356 
01357         const double d = sqrt( Radius / s2 );
01358 
01359         for( i = 0; i < ParamCount; i++ )
01360         {
01361             Params[ i ] += (ptype) ( NewValues[ i ] * d );
01362         }
01363     }
01364 
01369 
01370     void generateSolPar( CBiteRnd& rnd )
01371     {
01372         DoEval = false;
01373         CBitePop* UpdPop;
01374 
01375         if( UseParOpt == 1 )
01376         {
01377             // Re-assign optimizer 2 based on comparison of its
01378             // efficiency with optimizer 1.
01379 
01380             UseParOpt = select( ParOpt2Sel, rnd );
01381         }
01382 
01383         if( UseParOpt == 0 )
01384         {
01385             const int sc = ParOpt.optimize( rnd, &NewCost, NewValues );
01386 
01387             if( sc > 0 )
01388             {
01389                 UseParOpt = 1; // On stall, select optimizer 2.
01390             }
01391 
01392             if( sc > ParamCount * 64 )
01393             {
01394                 ParOpt.init( rnd, getBestParams(), 0.5 );
01395                 ParOptPop.resetCurPopPos();
01396             }
01397 
01398             UpdPop = &ParOptPop;
01399         }
01400         else
01401         {
01402             const int sc = ParOpt2.optimize( rnd, &NewCost, NewValues );
01403 
01404             if( sc > 0 )
01405             {
01406                 UseParOpt = 0; // On stall, select optimizer 1.
01407             }
01408 
01409             if( sc > ParamCount * 16 )
01410             {
01411                 ParOpt2.init( rnd, getBestParams(), 1.0 );
01412                 ParOpt2Pop.resetCurPopPos();
01413             }
01414 
01415             UpdPop = &ParOpt2Pop;
01416         }
01417 
01418         int i;
01419 
01420         for( i = 0; i < ParamCount; i++ )
01421         {
01422             TmpParams[ i ] = (ptype) (( NewValues[ i ] -
01423                 MinValues[ i ]) * DiffValuesI[ i ]);
01424         }
01425 
01426         UpdPop -> updatePop( NewCost, TmpParams, false );
01427     }
01428 };
