01442         : ParamCount( 0 )
01443         , OptCount( 0 )
01444         , Opts( NULL )
01445     {
01446     }
01447 
01448     virtual ~CBiteOptDeep()
01449     {
01450         deleteBuffers();
01451     }
01452 
01453     virtual const double* getBestParams() const
01454     {
01455         return( BestOpt -> getBestParams() );
01456     }
