01506 
01507     void updateDims( const int aParamCount, const int M = 6,
01508         const int PopSize0 = 0 )
01509     {
01510         if( aParamCount == ParamCount && M == OptCount )
01511         {
01512             return;
01513         }
01514 
01515         deleteBuffers();
01516 
01517         ParamCount = aParamCount;
01518         OptCount = M;
01519         Opts = new CBiteOptWrap*[ OptCount ];
01520 
01521         int i;
01522 
01523         for( i = 0; i < OptCount; i++ )
01524         {
01525             Opts[ i ] = new CBiteOptWrap( this );
01526             Opts[ i ] -> updateDims( aParamCount, PopSize0 );
01527         }
01528     }
01529 
01538 
01539     void init( CBiteRnd& rnd, const double* const InitParams = NULL,
01540         const double InitRadius = 1.0 )
01541     {
01542         int i;
01543 
01544         for( i = 0; i < OptCount; i++ )
01545         {
01546             Opts[ i ] -> init( rnd, InitParams, InitRadius );
01547         }
01548 
01549         BestOpt = Opts[ 0 ];
01550         CurOpt = Opts[ 0 ];
01551         StallCount = 0;
01552     }
01553 
01562 
01563     int optimize( CBiteRnd& rnd )
01564     {
01565         if( OptCount == 1 )
01566         {
01567             StallCount = Opts[ 0 ] -> optimize( rnd );
01568 
01569             return( StallCount );
01570         }
01571 
01572         CBiteOptWrap* PushOpt;
01573 
01574         if( OptCount == 2 )
01575         {
01576             PushOpt = Opts[ CurOpt == Opts[ 0 ]];
01577         }
01578         else
01579         {
01580             while( true )
01581             {
01582                 PushOpt = Opts[ rnd.getInt( OptCount )];
01583 
01584                 if( PushOpt != CurOpt )
01585                 {
01586                     break;
01587                 }
01588             }
01589         }
01590 
01591         const int sc = CurOpt -> optimize( rnd, PushOpt );
01592 
01593         if( CurOpt -> getBestCost() <= BestOpt -> getBestCost() )
01594         {
01595             BestOpt = CurOpt;
01596         }
01597 
01598         if( sc == 0 )
01599         {
01600             StallCount = 0;
01601         }
01602         else
01603         {
01604             CurOpt = PushOpt;
01605             StallCount++;
01606         }
01607 
01608         return( StallCount );
01609     }
