01679 
01680 class CBiteOptMinimize : public CBiteOptDeep
01681 {
01682 public:
01683     int N; 
01684     biteopt_func f; 
01685     void* data; 
01686     const double* lb; 
01687     const double* ub; 
01688 
01689     virtual void getMinValues( double* const p ) const
01690     {
01691         memcpy( p, lb, N * sizeof( p[ 0 ]));
01692     }
01693 
01694     virtual void getMaxValues( double* const p ) const
01695     {
01696         memcpy( p, ub, N * sizeof( p[ 0 ]));
01697     }
01698 
01699     virtual double optcost( const double* const p )
01700     {
01701         return(( *f )( N, p, data ));
01702     }
01703 };
01704 
01732 
01733 inline int biteopt_minimize( const int N, biteopt_func f, void* data,
01734     const double* lb, const double* ub, double* x, double* minf,
01735     const int iter, const int M = 1, const int attc = 10,
01736     const int stopc = 0, biteopt_rng rf = 0, void* rdata = 0,
01737     double* f_minp = 0 )
01738 {
01739     CBiteOptMinimize opt;
01740     opt.N = N;
01741     opt.f = f;
01742     opt.data = data;
01743     opt.lb = lb;
01744     opt.ub = ub;
01745     opt.updateDims( N, M );
01746 
01747     CBiteRnd rnd;
01748     rnd.init( 1, rf, rdata );
01749 
01750     const int sct = ( stopc <= 0 ? 0 : 128 * N * stopc );
01751     const int useiter = (int) ( iter * sqrt( (double) M ));
01752     int evals = 0;
01753     int k;
01754 
01755     for( k = 0; k < attc; k++ )
01756     {
01757         opt.init( rnd );
01758 
01759         bool IsFinished = false;
01760         int i;
01761 
01762         for( i = 0; i < useiter; i++ )
01763         {
01764             const int sc = opt.optimize( rnd );
01765 
01766             if( f_minp != 0 && opt.getBestCost() <= *f_minp )
01767             {
01768                 evals++;
01769                 IsFinished = true;
01770                 break;
01771             }
01772 
01773             if( sct > 0 && sc >= sct )
01774             {
01775                 evals++;
01776                 break;
01777             }
01778         }
01779 
01780         evals += i;
01781 
01782         if( k == 0 || opt.getBestCost() <= *minf )
01783         {
01784             memcpy( x, opt.getBestParams(), N * sizeof( x[ 0 ]));
01785             *minf = opt.getBestCost();
01786         }
01787 
01788         if( IsFinished )
01789         {
01790             break;
01791         }
01792     }
01793 
01794     return( evals );
01795 }
