00119     void updateWeights( const int UsePopSize )
00120     {
00121         // Calculate weights for centroid and covariance calculation.
00122 
00123         const double lm = 1.0 / (int) ceil( UsePopSize * EvalFac );
00124         double s = 0.0;
00125         int i;
00126 
00127         for( i = 0; i < UsePopSize; i++ )
00128         {
00129             const double l = 1.0 - i * lm;
00130             WPopCent[ i ] = pow( l, CentPow );
00131             s += WPopCent[ i ];
00132         }
00133 
00134         s = 1.0 / s;
00135 
00136         for( i = 0; i < UsePopSize; i++ )
00137         {
00138             WPopCent[ i ] *= s;
00139             WPopCov[ i ] = sqrt( WPopCent[ i ]);
00140         }
00141     }
