00180     void update( const CBitePop& ExtPop )
00181     {
00182         const int UsePopSize = ExtPop.getCurPopSize();
00183         const double** const ExtParams = ExtPop.getPopParams();
00184 
00185         updateWeights( UsePopSize );
00186 
00187         // Prepare PopParams (vector of per-parameter population deviations),
00188         // later used to calculate weighted covariances, use current centroid
00189         // vector.
00190 
00191         int i;
00192         int j;
00193 
00194         for( i = 0; i < ParamCount; i++ )
00195         {
00196             double* const op = PopParams[ i ];
00197             const double c = CentParams[ i ];
00198 
00199             for( j = 0; j < UsePopSize; j++ )
00200             {
00201                 op[ j ] = ( ExtParams[ j ][ i ] - c ) * WPopCov[ j ];
00202             }
00203         }
00204 
00205         // Store older centroid and calculate new centroid.
00206 
00207         copyParams( PrevCentParams, CentParams );
00208         calcCent( ExtParams, UsePopSize );
00209 
00210         // Update covariance matrix, the left-handed triangle only. Uses leaky
00211         // integrator averaging filter. "avgc" selects corner frequency
00212         // of the filter.
00213 
00214         // Select covariance update rate based on geometry parameter,
00215         // depends on sphericity.
00216 
00217         const double spc2 = spc * spc;
00218         const double spc3 = spc2 * spc;
00219         const double spc4 = spc2 * spc2;
00220 
00221         const double avgc = 1.0 - pow( 0.01,
00222             ( CovUpdFast * spc4 + CovUpdSlow * ( 1.0 - spc4 )) /
00223             ( UsePopSize * EvalFac ));
00224 
00225         // Perform covariance matrix update using a leaky integrator filter.
00226 
00227         for( j = 0; j < ParamCount; j++ )
00228         {
00229             double* const cp = CovParams[ j ];
00230 
00231             for( i = 0; i <= j; i++ )
00232             {
00233                 const double cov = dotp( PopParams[ j ], PopParams[ i ],
00234                     UsePopSize );
00235 
00236                 cp[ i ] += ( cov - cp[ i ]) * avgc;
00237             }
00238         }
00239 
00240         // Calculate rotation matrix and standard deviations.
00241 
00242         eigen();
00243 
00244         // Calculate distribution's geometry parameter.
00245 
00246         double maxd = 1e-100; // Maximal deviation among all parameters.
00247 
00248         for( i = 0; i < ParamCount; i++ )
00249         {
00250             if( DParams[ i ] > maxd )
00251             {
00252                 maxd = DParams[ i ];
00253             }
00254         }
00255 
00256         maxd = 1.0 / maxd;
00257         spc = 0.0;
00258 
00259         for( i = 0; i < ParamCount; i++ )
00260         {
00261             TmpParams[ i ] = DParams[ i ] * maxd;
00262             spc += TmpParams[ i ];
00263         }
00264 
00265         spc /= ParamCount;
00266 
00267         // Apply extension or contraction to positive and negative halfs of
00268         // standard deviations, depending on centroid step size.
00269 
00270         for( i = 0; i < ParamCount; i++ )
00271         {
00272             PrevCentParams[ i ] = CentParams[ i ] - PrevCentParams[ i ];
00273         }
00274 
00275         ortnc( PrevCentParams, DParamsN ); // Orthogonalize centroid step.
00276 
00277         for( i = 0; i < ParamCount; i++ )
00278         {
00279             const double d = DParamsN[ i ];
00280 
00281             DParamsN[ i ] = DParams[ i ] - d;
00282             DParams[ i ] += d;
00283         }
00284 
00285         // Modify standard deviations, apply reduction, or extension to
00286         // overly shrunk dimensions. Depends on sphericity.
00287 
00288         for( i = 0; i < ParamCount; i++ )
00289         {
00290             const double c = 1.0 - TmpParams[ i ];
00291             const double m = ( SigmaMulBase2 +
00292                 ( SigmaMulExp2 - SigmaMulBase2 ) * c * c * c ) *
00293                 ( 1.0 - spc3 ) + SigmaMulBase1 * spc3;
00294 
00295             DParams[ i ] *= m;
00296             DParamsN[ i ] *= m;
00297         }
00298     }
