00493     static void tred2( const int n, double* d, double** V, double* e )
00494     {
00495         //  This is derived from the Algol procedures tred2 by
00496         //  Bowdler, Martin, Reinsch, and Wilkinson, Handbook for
00497         //  Auto. Comp., Vol.ii-Linear Algebra, and the corresponding
00498         //  Fortran subroutine in EISPACK.
00499 
00500         for (int j = 0; j < n; j++) {
00501             d[j] = V[n-1][j];
00502         }
00503 
00504         // Householder reduction to tridiagonal form.
00505 
00506         for (int i = n-1; i > 0; i--) {
00507 
00508             // Scale to avoid under/overflow.
00509 
00510             double scale = 0.0;
00511             double h = 0.0;
00512             for (int k = 0; k < i; k++) {
00513                 scale = scale + abs(d[k]);
00514             }
00515             if (scale == 0.0) {
00516                 e[i] = d[i-1];
00517                 for (int j = 0; j < i; j++) {
00518                     d[j] = V[i-1][j];
00519                     V[i][j] = 0.0;
00520                     V[j][i] = 0.0;
00521                 }
00522             } else {
00523 
00524                 // Generate Householder vector.
00525 
00526                 for (int k = 0; k < i; k++) {
00527                     d[k] /= scale;
00528                     h += d[k] * d[k];
00529                 }
00530                 double f = d[i-1];
00531                 double g = sqrt(h);
00532                 if (f > 0) {
00533                     g = -g;
00534                 }
00535                 e[i] = scale * g;
00536                 h = h - f * g;
00537                 d[i-1] = f - g;
00538                 for (int j = 0; j < i; j++) {
00539                     e[j] = 0.0;
00540                 }
00541 
00542                 // Apply similarity transformation to remaining columns.
00543 
00544                 for (int j = 0; j < i; j++) {
00545                     f = d[j];
00546                     V[j][i] = f;
00547                     g = e[j] + V[j][j] * f;
00548                     for (int k = j+1; k <= i-1; k++) {
00549                         g += V[k][j] * d[k];
00550                         e[k] += V[k][j] * f;
00551                     }
00552                     e[j] = g;
00553                 }
00554                 f = 0.0;
00555                 for (int j = 0; j < i; j++) {
00556                     e[j] /= h;
00557                     f += e[j] * d[j];
00558                 }
00559                 double hh = f / (h + h);
00560                 for (int j = 0; j < i; j++) {
00561                     e[j] -= hh * d[j];
00562                 }
00563                 for (int j = 0; j < i; j++) {
00564                     f = d[j];
00565                     g = e[j];
00566                     for (int k = j; k <= i-1; k++) {
00567                         V[k][j] -= (f * e[k] + g * d[k]);
00568                     }
00569                     d[j] = V[i-1][j];
00570                     V[i][j] = 0.0;
00571                 }
00572             }
00573             d[i] = h;
00574         }
00575 
00576         // Accumulate transformations.
00577 
00578         for (int i = 0; i < n-1; i++) {
00579             V[n-1][i] = V[i][i];
00580             V[i][i] = 1.0;
00581             double h = d[i+1];
00582             if (h != 0.0) {
00583                 for (int k = 0; k <= i; k++) {
00584                     d[k] = V[k][i+1] / h;
00585                 }
00586                 for (int j = 0; j <= i; j++) {
00587                     double g = 0.0;
00588                     for (int k = 0; k <= i; k++) {
00589                         g += V[k][i+1] * V[k][j];
00590                     }
00591                     for (int k = 0; k <= i; k++) {
00592                         V[k][j] -= g * d[k];
00593                     }
00594                 }
00595             }
00596             for (int k = 0; k <= i; k++) {
00597                 V[k][i+1] = 0.0;
00598             }
00599         }
00600         for (int j = 0; j < n; j++) {
00601             d[j] = V[n-1][j];
00602             V[n-1][j] = 0.0;
00603         }
00604         V[n-1][n-1] = 1.0;
00605         e[0] = 0.0;
00606     }
