00612     static double hypot_( double a, double b )
00613     {
00614         const double fa = fabs(a);
00615         const double fb = fabs(b);
00616         double r;
00617         if (fa > fb)
00618         {
00619             r = b/a;
00620             r = fa*sqrt(1.0+r*r);
00621         }
00622         else
00623         if (b != 0.0)
00624         {
00625             r = a/b;
00626             r = fb*sqrt(1.0+r*r);
00627         }
00628         else
00629         {
00630             r = 0.0;
00631         }
00632         return r;
00633     }
