00649     static void tql2( const int n, double* d, double** V, double* e )
00650     {
00651         //  This is derived from the Algol procedures tql2, by
00652         //  Bowdler, Martin, Reinsch, and Wilkinson, Handbook for
00653         //  Auto. Comp., Vol.ii-Linear Algebra, and the corresponding
00654         //  Fortran subroutine in EISPACK.
00655 
00656         for (int i = 1; i < n; i++) {
00657             e[i-1] = e[i];
00658         }
00659         e[n-1] = 0.0;
00660 
00661         double f = 0.0;
00662         double tst1 = 0.0;
00663         double eps = pow(2.0,-52.0);
00664         for (int l = 0; l < n; l++) {
00665 
00666             // Find small subdiagonal element
00667 
00668             tst1 = max_(tst1,abs(d[l]) + abs(e[l]));
00669             int m = l;
00670             while (m < n) {
00671                 if (abs(e[m]) <= eps*tst1) {
00672                     break;
00673                 }
00674                 m++;
00675             }
00676 
00677             // If m == l, d[l] is an eigenvalue,
00678             // otherwise, iterate.
00679 
00680             if (m > l) {
00681                 do {
00682                     // Compute implicit shift
00683 
00684                     double g = d[l];
00685                     double p = (d[l+1] - g) / (2.0 * e[l]);
00686                     double r = hypot_(p,1.0);
00687                     if (p < 0) {
00688                         r = -r;
00689                     }
00690                     d[l] = e[l] / (p + r);
00691                     d[l+1] = e[l] * (p + r);
00692                     double dl1 = d[l+1];
00693                     double h = g - d[l];
00694                     for (int i = l+2; i < n; i++) {
00695                         d[i] -= h;
00696                     }
00697                     f = f + h;
00698 
00699                     // Implicit QL transformation.
00700 
00701                     p = d[m];
00702                     double c = 1.0;
00703                     double c2 = c;
00704                     double c3 = c;
00705                     double el1 = e[l+1];
00706                     double s = 0.0;
00707                     double s2 = 0.0;
00708                     for (int i = m-1; i >= l; i--) {
00709                         c3 = c2;
00710                         c2 = c;
00711                         s2 = s;
00712                         g = c * e[i];
00713                         h = c * p;
00714                         r = hypot_(p,e[i]);
00715                         e[i+1] = s * r;
00716                         s = e[i] / r;
00717                         c = p / r;
00718                         p = c * d[i] - s * g;
00719                         d[i+1] = h + s * (c * g + s * d[i]);
00720 
00721                         // Accumulate transformation.
00722 
00723                         for (int k = 0; k < n; k++) {
00724                             h = V[k][i+1];
00725                             V[k][i+1] = s * V[k][i] + c * h;
00726                             V[k][i] = c * V[k][i] - s * h;
00727                         }
00728                     }
00729                     p = -s * s2 * c3 * el1 * e[l] / dl1;
00730                     e[l] = s * p;
00731                     d[l] = c * p;
00732 
00733                     // Check for convergence.
00734 
00735                 } while (abs(e[l]) > eps*tst1);
00736             }
00737             d[l] = d[l] + f;
00738             e[l] = 0.0;
00739         }
00740     }
