00747     void eigen()
00748     {
00749         // Replicate left-handed triangular matrix.
00750 
00751         int i;
00752         int j;
00753 
00754         for( j = 0; j < ParamCount; j++ )
00755         {
00756             for( i = 0; i <= j; i++ )
00757             {
00758                 BParams[ j ][ i ] = BParams[ i ][ j ] = CovParams[ j ][ i ];
00759             }
00760         }
00761 
00762         // Calculate eigen-pairs.
00763 
00764         tred2( ParamCount, DParams, BParams, TmpParams );
00765         tql2( ParamCount, DParams, BParams, TmpParams );
00766 
00767         for( i = 0; i < ParamCount; i++ )
00768         {
00769             if( DParams[ i ] < 1e-60 )
00770             {
00771                 DParams[ i ] = 1e-30;
00772             }
00773             else
00774             {
00775                 DParams[ i ] = sqrt( DParams[ i ]);
00776             }
00777         }
00778     }
