00787     void calcCent( const double** const SortedParams, const int UsePopSize )
00788     {
00789         const double* ip = SortedParams[ 0 ];
00790         double w = WPopCent[ 0 ];
00791         int i;
00792 
00793         for( i = 0; i < ParamCount; i++ )
00794         {
00795             CentParams[ i ] = ip[ i ] * w;
00796         }
00797 
00798         int j;
00799 
00800         for( j = 1; j < UsePopSize; j++ )
00801         {
00802             ip = SortedParams[ j ];
00803             w = WPopCent[ j ];
00804 
00805             for( i = 0; i < ParamCount; i++ )
00806             {
00807                 CentParams[ i ] += ip[ i ] * w;
00808             }
00809         }
00810     }
