00821     double dotp( const double* const p1, const double* const p2,
00822         const int c ) const
00823     {
00824         double s = 0.0;
00825         int i;
00826 
00827         for( i = 0; i < c; i++ )
00828         {
00829             s += p1[ i ] * p2[ i ];
00830         }
00831 
00832         return( s );
