00028 static vec sequence(double start, double end, double step) {
00029     int size = (int) ((end - start) / step + 1);
00030     vec d(size);
00031     double value = start;
00032     for (int r = 0; r < size; r++) {
00033         d(r) = value;
00034         value += step;
00035     }
00036     return d;
00037 }
00038 
00039 class CrfmnesOptimizer {
00040 
00041 public:
00042 
00043     CrfmnesOptimizer(int64_t  runid_, Fitness* fitfun_, int dim_, vec m_, double sigma_, int lamb_,
00044             int maxEvaluations_, double stopfitness_,
00045             double penalty_coef_, bool use_constraint_violation_, int64_t  seed) {
00046 
00047         runid = runid_;
00048         fitfun = fitfun_;
00049         dim = dim_;
00050         m = fitfun->encode(m_);
00051         sigma = sigma_;
00052         lamb = lamb_;
00053         mu = lamb_/2;
00054         maxEvaluations = maxEvaluations_;
00055         stopfitness = stopfitness_;
00056         penalty_coef = penalty_coef_ > 0 ? penalty_coef_ : 1e5;
00057         use_constraint_violation = use_constraint_violation_;
00058         rs = new pcg64(seed);
00059 
00060         stop = 0;
00061         v = normalVec(dim, *rs) / sqrt(dim);
00062         D = constant(dim, 1);
00063         w_rank_hat = ((log(sequence(1, lamb, 1).array()) * -1.) + log(mu + 1)).cwiseMax(0);
00064         w_rank = (w_rank_hat / w_rank_hat.sum()).array() - (1. / lamb);
00065         vec wlamb = w_rank.array() + (1. / lamb);
00066         mueff = 1. / (wlamb.transpose() * wlamb)(0,0);
00067         cs = (mueff + 2.) / (dim + mueff + 5.);
00068         cc = (4. + mueff / dim) / (dim + 4. + 2. * mueff / dim);
00069         c1_cma = 2. / (pow(dim + 1.3, 2) + mueff);
00070         // initialization
00071         chiN = sqrt(dim) * (1. - 1. / (4. * dim) + 1. / (21. * dim * dim));
00072         pc = zeros(dim);
00073         ps = zeros(dim);
00074         // distance weight parameter
00075         h_inv = get_h_inv(dim);
00076         // learning rate
00077         eta_m = 1.0;
00078         eta_move_sigma = 1.;
00079 
00080         g = 0;
00081         no_of_evals = 0;
00082         z = mat(dim, lamb);
00083         f_best = INFINITY;
00084         x_best = vec(dim);
00085     };
00086 
00087     virtual ~CrfmnesOptimizer() {
00088         delete rs;
00089     }
00090 
00091     void doOptimize() {
00092         // -------------------- Generation Loop --------------------------------
00093         for (iterations = 1; fitfun->evaluations() < maxEvaluations && !fitfun->terminate();
00094                 iterations++) {
00095             // generate and evaluate lamb offspring
00096             try {
00097                 mat xs = ask();
00098                 vec evs(lamb);
00099                 fitfun->values(xs, evs);
00100                 tell(evs);
00101             } catch (std::exception &e) {
00102                  stop = -1;
00103             }
00104             if (stop != 0)
00105                 return;
00106         }
00107     }
00108 
00109     mat getPopulation() {
00110          return xs_no_sort;
00111     }
00112 
00113     vec getBestX() {
00114         return x_best;
00115     }
00116 
00117     double getBestValue() {
00118         return f_best;
00119     }
00120 
00121     double getIterations() {
00122         return iterations;
00123     }
00124 
00125     int getStop() {
00126         return stop;
00127     }
00128 
00129     Fitness* getFitfun() {
00130         return fitfun;
00131     }
