00141     mat ask() {
00142         mat zhalf = normal(dim, mu, *rs);
00143         for (int i = 0; i < lamb; i++) {
00144             if (i < mu) z.col(i) = zhalf.col(i);
00145             else z.col(i) = -zhalf.col(i-mu);
00146         }
00147         normv = v.norm();
00148         normv2 = normv*normv;
00149         vbar = v / normv;
00150         y = z + ((sqrt(1 + normv2) - 1.) * (vbar  * (vbar.transpose() * z)));
00151         x = ((sigma * y.array()).colwise() * D.array()).colwise() + m.array();
00152         return x;
00153     }
00154 
00155     void tell(vec &evs) {
00156         evals_no_sort = vec(evs);
00157         for (int k = 0; k < lamb; k++) {
00158             if (!isfinite(evals_no_sort[k]))
00159                 evals_no_sort[k] = DBL_MAX;
00160         }
00161         xs_no_sort = mat(x);
00162         ivec sorted_indices;
00163         if (use_constraint_violation) {
00164             vec violations = fitfun->violations(x, penalty_coef);
00165             sorted_indices = sort_indices_by(evals_no_sort + violations, z);
00166         } else
00167             sorted_indices = sort_indices_by(evals_no_sort, z);
00168         int best_eval_id = sorted_indices[0];
00169         double f_best_ = evals_no_sort[best_eval_id];
00170 
00171         z = mat(z)(Eigen::indexing::all, sorted_indices);
00172         y = mat(y)(Eigen::indexing::all, sorted_indices);
00173         x = mat(x)(Eigen::indexing::all, sorted_indices);
00174         no_of_evals += lamb;
00175         g += 1;
00176         if (f_best_ < f_best) {
00177             f_best = f_best_;
00178             x_best = fitfun->decode(xs_no_sort.col(best_eval_id));
00179             if (f_best < stopfitness)
00180                 stop = 1;
00181             //cout << f_best << endl;
00182         }
00183         // This operation assumes that if the solution is infeasible, infinity comes in as input.
00184         double lambF = 0;
00185         for (int k = 0; k < lamb; k++)
00186             if (evals_no_sort[k] < DBL_MAX) lambF++;
00187         // evolution path p_sigma
00188         ps = (1 - cs) * ps + sqrt(cs * (2. - cs) * mueff) * (z * w_rank);
00189         double ps_norm = ps.norm();
00190         // distance weight
00191         vec w_tmp(lamb);
00192         for (int k = 0; k < lamb; k++)
00193             w_tmp[k] = w_rank_hat[k] * w_dist_hat(z.col(k), lambF);
00194         vec weights_dist = (w_tmp / w_tmp.sum()).array() - 1. / lamb;
00195         // switching weights and learning rate
00196         vec weights = ps_norm >= chiN ? weights_dist : w_rank;
00197         double eta_sigma = ps_norm >= chiN ? eta_move_sigma :
00198                 (ps_norm >= 0.1 * chiN ? eta_stag_sigma(lambF) : eta_conv_sigma(lambF));
00199         // update pc, m
00200         vec wxm = (x.array().colwise() - m.array()).matrix() * weights;
00201         pc = (1. - cc) * pc + sqrt(cc * (2. - cc) * mueff) * wxm / sigma;
00202         m += eta_m * wxm;
00203         // calculate s, t
00204         // step1
00205         double normv4 = normv2 * normv2;
00206         mat exY(dim, lamb+1);
00207         for (int k = 0; k < lamb; k++)
00208             exY.col(k) = y.col(k);
00209         exY.col(lamb) = pc.array() / D.array();
00210         mat yy = exY.array() * exY.array();  // dim x lamb+1
00211         vec ip_yvbar = vbar.transpose() * exY;
00212         mat yvbar = exY.array().colwise() * vbar.array(); // dim x lamb+1. exYのそれぞれの列にvbarがかかる
00213         double gammav = 1. + normv2;
00214         vec vbarbar = vbar.array() * vbar.array();
00215         double alphavd = min(1., sqrt(normv4 + (2 * gammav - sqrt(gammav)) / vbarbar.maxCoeff()) / (2. + normv2));  // scalar
00216         mat vbar_bc = zeros(dim, lamb+1).colwise() + vbar; // broadcasting vbar
00217         vec ibg = (ip_yvbar.array()*ip_yvbar.array()) + gammav;
00218         mat t = (exY.array().rowwise() * ip_yvbar.transpose().array()) -
00219             (vbar_bc.array().rowwise() * ibg.transpose().array()) / 2.;
00220         double b = -(1 - alphavd * alphavd) * normv4 / gammav + 2 * alphavd * alphavd;
00221         vec H = constant(dim, 2.) - (b + 2 * alphavd * alphavd) * vbarbar;  // dim x 1
00222         vec invH = 1. / H.array();
00223         mat s_step1 = yy.array() - normv2 / gammav * (yvbar.array().rowwise() * ip_yvbar.transpose().array()).array()
00224                 - constant(dim, lamb+1, 1.).array(); // dim x lamb+1
00225         vec ip_vbart = vbar.transpose() * t;  // 1 x lamb+1
00226         mat s_step2 = s_step1.array()
00227                 - (alphavd / gammav * ((2 + normv2) * (t.array().colwise() * vbar.array()).array()
00228                         - (normv2 * (vbarbar * ip_vbart.transpose())).array()));  // dim x lamb+1
00229         vec invHvbarbar = invH.array() * vbarbar.array();
00230         vec ip_s_step2invHvbarbar = invHvbarbar.transpose() * s_step2;  // 1 x lamb+1
00231 
00232         double div = 1 + b * (vbarbar.transpose() * invHvbarbar)(0,0);
00233         if (div == 0)
00234 //            div = 1E-13;int
00235             throw std::invalid_argument( "division by 0" );
00236         mat s = (s_step2.array().colwise() * invH.array()).array()
00237                 - ((b / div) * (invHvbarbar * ip_s_step2invHvbarbar.transpose())).array();  // dim x lamb+1
00238 
00239         vec ip_svbarbar = vbarbar.transpose() * s;  // 1 x lamb+1
00240         t = t.array() - alphavd * ((2 + normv2) * (s.array().colwise() * vbar.array()).array()
00241                                     - (vbar * ip_svbarbar.transpose()).array());  // dim x lamb+1
00242         // update v, D
00243         vec exw(lamb+1);
00244         for (int k = 0; k < lamb; k++)
00245             exw[k] = eta_B(lambF) * weights[k];
00246         exw[lamb] = c1(lambF);
00247 
00248         v = v.array() + (t * exw).array() / normv;
00249         D = D.array() + (s * exw).array() * D.array();
00250 
00251         // calculate detA
00252         if (D.minCoeff() < 0) {
00253             //throw std::invalid_argument( "D < 0" );
00254             stop = -1;
00255             return;
00256         }
00257         double nthrootdetA = cexp(D.array().log().sum() / dim + log(1 + (v.transpose() * v)(0,0)) / (2 * dim));
00258         D = D.array() / nthrootdetA;
00259         // update sigma
00260         double G_s = (((z.array() * z.array()).array() - constant(dim, lamb, 1.).array()).matrix() * weights).sum() / dim;
00261         sigma = sigma * cexp(eta_sigma / 2 * G_s);
00262     }
00263 
00264 private:
00265 
00266     double cexp(double a) { return exp(min(a, 100.0)); } // avoid overflow
00267     double c1(double lambF) { return c1_cma * (dim - 5) / 6 * (lambF / lamb); }
00268     double eta_B(double lambF) { return tanh((min(0.02 * lambF, 3 * log(dim)) + 5) / (0.23 * dim + 25)); }
00269     double alpha_dist(double lambF) { return h_inv * min(1., sqrt(((double)lamb) / dim)) * sqrt(((double)lambF) / lamb); }
00270     double w_dist_hat(mat z, double lambF) { return cexp(alpha_dist(lambF) * z.norm()); }
00271     double eta_stag_sigma(double lambF) { return tanh((0.024 * lambF + 0.7 * dim + 20.) / (dim + 12.)); }
00272     double eta_conv_sigma(double lambF) { return 2. * tanh((0.025 * lambF + 0.75 * dim + 10.) / (dim + 4.)); }
00273     double f(double a) { return  ((1. + a * a) * cexp(a * a / 2.) / 0.24) - 10. - dim; }
00274     double f_prime(double a) { return  (1. / 0.24) * a * cexp(a * a / 2.) * (3. + a * a); }
00275 
00276     double get_h_inv(int dim) {
00277         double h_inv = 1.0;
00278         while (abs(f(h_inv)) > 1e-10)
00279             h_inv = h_inv - 0.5 * (f(h_inv) / f_prime(h_inv));
00280         return h_inv;
00281     }
00282 
00283     int num_feasible(const vec &evals) {
00284         int n = 0;
00285         for (int i = 0; i < evals.size(); i++)
00286             if (evals[i] != INFINITY) n++;
00287         return n;
00288     }
00289 
00290     ivec sort_indices_by(const vec &evals, mat z) {
00291         int lam = evals.size();
00292         ivec sorted_indices = sort_index(evals);
00293         vec sorted_evals = evals(sorted_indices);
00294         int no_of_feasible_solutions = num_feasible(sorted_evals);
00295         if (no_of_feasible_solutions != lam) {
00296             vec distances(lam - no_of_feasible_solutions);
00297             int n = 0;
00298             for (int i = 0; i < lam; i++)
00299                 if (evals[i] == INFINITY) {
00300                     distances[n] = z.col(i).squaredNorm();
00301                     n++;
00302                 }
00303             ivec indices_sorted_by_distance = sort_index(distances);
00304             for (int i = no_of_feasible_solutions; i < lam; i++)
00305                 sorted_indices[i] = sorted_indices[no_of_feasible_solutions
00306                            + indices_sorted_by_distance[i-no_of_feasible_solutions]];
00307         }
00308         return sorted_indices;
00309     }
