00323     vec w_rank_hat;
00324     vec  w_rank;
00325     double mueff;
00326     double cs;
00327     double cc;
00328     double c1_cma;
00329     // initialization
00330     double chiN;
00331     vec pc;
00332     vec ps;
00333     // distance weight parameter
00334     double h_inv;
00335     // learning rate
00336     double eta_m;
00337     double eta_move_sigma;
00338 
