00365 void optimizeCRFMNES_C(int64_t  runid, callback_parallel func_par, int dim,
00366         double *init, double *lower, double *upper, double sigma,
00367         int maxEvals, double stopfitness, int popsize,
00368         int64_t  seed, double penalty_coef, bool use_constraint_violation, bool normalize, double* res) {
00369 
00370     vec guess(dim), lower_limit(dim), upper_limit(dim);
00371     for (int i = 0; i < dim; i++) // guess is mandatory
00372      guess[i] = init[i];
00373     if (lower != NULL && upper != NULL) {
00374         for (int i = 0; i < dim; i++) {
00375             guess[i] = init[i];
00376             lower_limit[i] = lower[i];
00377             upper_limit[i] = upper[i];
00378         }
00379     } else {
00380         lower_limit.resize(0);
00381         upper_limit.resize(0);
00382         normalize = false;
00383     }
00384 
00385     Fitness fitfun(noop_callback, func_par, dim, 1, lower_limit, upper_limit);
00386     fitfun.setNormalize(normalize);
00387 
00388     CrfmnesOptimizer opt(runid, &fitfun, dim, guess, sigma, popsize,
00389             maxEvals, stopfitness, penalty_coef, use_constraint_violation, seed);
00390     try {
00391         opt.doOptimize();
00392     } catch (std::exception &e) {
00393          cout << e.what() << endl;
00394     }
00395     vec bestX = opt.getBestX();
00396     double bestY = opt.getBestValue();
00397     for (int i = 0; i < dim; i++)
00398         res[i] = bestX[i];
00399     res[dim] = bestY;
00400     res[dim + 1] = fitfun.evaluations();
00401     res[dim + 2] = opt.getIterations();
00402     res[dim + 3] = opt.getStop();
00403 }
