00405 uintptr_t initCRFMNES_C(int64_t  runid, int dim,
00406         double *init, double *lower, double *upper, double sigma,
00407         int popsize, int64_t  seed, double penalty_coef, bool use_constraint_violation, bool normalize) {
00408 
00409      vec guess(dim), lower_limit(dim), upper_limit(dim);
00410      for (int i = 0; i < dim; i++) // guess is mandatory
00411          guess[i] = init[i];
00412      if (lower != NULL && upper != NULL) {
00413         for (int i = 0; i < dim; i++) {
00414             guess[i] = init[i];
00415             lower_limit[i] = lower[i];
00416             upper_limit[i] = upper[i];
00417         }
00418      } else {
00419          lower_limit.resize(0);
00420          upper_limit.resize(0);
00421          normalize = false;
00422      }
00423      Fitness* fitfun = new Fitness(noop_callback, noop_callback_par, dim, 1, lower_limit, upper_limit);
00424      fitfun->setNormalize(normalize);
00425 
00426      CrfmnesOptimizer* opt = new CrfmnesOptimizer(runid, fitfun, dim, guess, sigma, popsize,
00427              0, -DBL_MAX, penalty_coef, use_constraint_violation, seed);
00428 
00429      return (uintptr_t) opt;
00430 }
