00439 void askCRFMNES_C(uintptr_t ptr, double* xs) {
00440     CrfmnesOptimizer *opt = (CrfmnesOptimizer*) ptr;
00441     int n = opt->getDim();
00442     int lamb = opt->getPopsize();
00443     mat popX = opt->ask();
00444     Fitness* fitfun = opt->getFitfun();
00445     for (int p = 0; p < lamb; p++) {
00446         vec x = fitfun->getClosestFeasible(fitfun->decode(popX.col(p)));
00447         for (int i = 0; i < n; i++)
00448             xs[p * n + i] = x[i];
00449     }
00450 }
