00471 int populationCRFMNES_C(uintptr_t ptr, double* xs) {
00472     CrfmnesOptimizer *opt = (CrfmnesOptimizer*) ptr;
00473     int dim = opt->getDim();
00474     int lamb = opt->getPopsize();
00475     mat popX = opt->getPopulation();
00476     for (int p = 0; p < lamb; p++) {
00477         vec x = popX.col(p);
00478         for (int i = 0; i < dim; i++)
00479             x[i] = xs[p * dim + i];
00480     }
00481     return opt->getStop();
00482 }
