00484 int resultCRFMNES_C(uintptr_t ptr, double* res) {
00485     CrfmnesOptimizer *opt = (CrfmnesOptimizer*) ptr;
00486     vec bestX = opt->getBestX();
00487     double bestY = opt->getBestValue();
00488     int n = bestX.size();
00489     for (int i = 0; i < bestX.size(); i++)
00490         res[i] = bestX[i];
00491     res[n] = bestY;
00492     Fitness* fitfun = opt->getFitfun();
00493     res[n + 1] = fitfun->evaluations();
00494     res[n + 2] = opt->getIterations();
00495     res[n + 3] = opt->getStop();
00496     return opt->getStop();
