00069     void updateDims( const int aParamCount, const int PopSize0 = 0 )
00070     {
00071         const int aPopSize = ( PopSize0 > 0 ? PopSize0 :
00072             ( aParamCount + 1 ) * 4 );
00073 
00074         if( aParamCount == ParamCount && aPopSize == PopSize )
00075         {
00076             return;
00077         }
00078 
00079         N = aParamCount;
00080         M = aPopSize;
00081         M1 = aPopSize - 1;
00082         M1i = 1.0 / M1;
00083 
