00167     int optimize( CBiteRnd& rnd, double* const OutCost = NULL,
00168         double* const OutValues = NULL )
00169     {
00170         int i;
00171 
00172         if( DoInitEvals )
00173         {
00174             y[ CurPopPos ] = eval( rnd, x[ CurPopPos ], OutCost, OutValues );
00175 
00176             if( y[ CurPopPos ] < y[ xlo ])
00177             {
00178                 xlo = CurPopPos;
00179             }
00180 
00181             CurPopPos++;
00182 
00183             if( CurPopPos == M )
00184             {
00185                 DoInitEvals = false;
00186                 calccent();
00187             }
