00191 
00192         StallCount++;
00193 
00194         const double sn = 0.5 * sqrt( ParamCountI );
00195 
00196         static const double alpha = 1.0; // Reflection coeff.
00197         const double gamma = 1.5 + sn; // Expansion coeff.
00198         const double rho = -0.75 + sn; // Contraction coeff.
00199         const double sigma = 1.0 - sn; // Reduction coeff.
00200 
00201         double* const xH = x[ xhi ]; // Highest cost parameter vector.
00202 
00203         switch( State )
00204         {
00205             case stReflection:
00206             {
00207                 for( i = 0; i < N; i++ )
00208                 {
00209                     x1[ i ] = x0[ i ] + alpha * ( x0[ i ] - xH[ i ]);
00210                 }
00211 
00212                 y1 = eval( rnd, x1, OutCost, OutValues );
00213 
00214                 if( y1 > y[ xlo ] && y1 < y[ xhi2 ])
00215                 {
00216                     copy( x1, y1 );
00217                 }
00218                 else
00219                 {
00220                     if( y1 < y[ xlo ])
00221                     {
00222                         State = stExpansion;
00223                         StallCount--;
00224                     }
00225                     else
00226                     {
00227                         State = stContraction;
00228                     }
00229                 }
00230 
00231                 break;
00232             }
00233 
00234             case stExpansion:
00235             {
00236                 for( i = 0; i < N; i++ )
00237                 {
00238                     x2[ i ] = x0[ i ] + gamma * ( x0[ i ] - xH[ i ]);
00239                 }
00240 
00241                 const double y2 = eval( rnd, x2, OutCost, OutValues );
00242                 xlo = xhi;
00243 
00244                 if( y2 < y1 )
00245                 {
00246                     copy( x2, y2 );
00247                 }
00248                 else
00249                 {
00250                     copy( x1, y1 );
00251                 }
00252 
00253                 State = stReflection;
00254                 break;
00255             }
00256 
00257             case stContraction:
00258             {
00259                 for( i = 0; i < N; i++ )
00260                 {
00261                     x2[ i ] = x0[ i ] + rho * ( x0[ i ] - xH[ i ]);
00262                 }
00263 
00264                 const double y2 = eval( rnd, x2, OutCost, OutValues );
00265 
00266                 if( y2 < y[ xhi ])
00267                 {
00268                     if( y2 < y[ xlo ])
00269                     {
00270                         xlo = xhi;
00271                     }
00272 
00273                     copy( x2, y2 );
00274                     State = stReflection;
00275                 }
00276                 else
00277                 {
00278                     rx = x[ xlo ];
00279                     rj = 0;
00280                     State = stReduction;
00281                 }
00282 
00283                 break;
00284             }
00285 
00286             case stReduction:
00287             {
00288                 double* xx = x[ rj ];
00289 
00290                 if( xx == rx )
00291                 {
00292                     rj++;
00293                     xx = x[ rj ];
00294                 }
00295 
00296                 for( i = 0; i < N; i++ )
00297                 {
00298                     xx[ i ] = rx[ i ] + sigma * ( xx[ i ] - rx[ i ]);
00299                 }
00300 
00301                 y[ rj ] = eval( rnd, xx, OutCost, OutValues );
00302 
00303                 if( y[ rj ] < y[ xlo ])
00304                 {
00305                     xlo = rj;
00306                     StallCount = 0;
00307                 }
00308 
00309                 rj++;
00310 
00311                 if( rj == M || ( rj == M1 && x[ rj ] == rx ))
00312                 {
00313                     calccent();
00314                     State = stReflection;
00315                 }
00316 
00317                 break;
00318             }
00319         }
00320 
00321         return( StallCount );
