00281     void update( CBiteRnd& rnd )
00282     {
00283         static const double WCent[ 4 ] = { 4.5, 6.0, 7.5, 10.0 };
00284         static const double WRad[ 4 ] = { 14.0, 16.0, 18.0, 20.0 };
00285         static const double EvalFacs[ 3 ] = { 2.1, 2.0, 1.9 };
00286 
00287         const double CentFac = WCent[ select( CentPowSel, rnd )];
00288         const double RadFac = WRad[ select( RadPowSel, rnd )];
00289         EvalFac = EvalFacs[ select( EvalFacSel, rnd )];
00290 
00291         const double lm = 1.0 / curem;
00292         double s1 = 0.0;
00293         double s2 = 0.0;
00294         int i;
00295 
00296         for( i = 0; i < CurPopSize; i++ )
00297         {
00298             const double l = 1.0 - i * lm;
00299 
00300             const double v1 = pow( l, CentFac );
00301             WPopCent[ i ] = v1;
00302             s1 += v1;
00303 
00304             const double v2 = pow( l, RadFac );
00305             WPopRad[ i ] = v2;
00306             s2 += v2;
00307         }
00308 
00309         s1 = 1.0 / s1;
00310         s2 = 1.0 / s2;
00311 
00312         const double* ip = PopParams[ 0 ];
00313         double* const cp = CentParams;
00314         const double* const wc = WPopCent;
00315         double w = wc[ 0 ] * s1;
00316 
00317         for( i = 0; i < ParamCount; i++ )
00318         {
00319             cp[ i ] = ip[ i ] * w;
00320         }
00321 
00322         int j;
00323 
00324         for( j = 1; j < CurPopSize; j++ )
00325         {
00326             ip = PopParams[ j ];
00327             w = wc[ j ] * s1;
00328 
00329             for( i = 0; i < ParamCount; i++ )
00330             {
00331                 cp[ i ] += ip[ i ] * w;
00332             }
00333         }
00334 
00335         const double* const rc = WPopRad;
00336         Radius = 0.0;
00337 
00338         for( j = 0; j < CurPopSize; j++ )
00339         {
00340             ip = PopParams[ j ];
00341             double s = 0.0;
00342 
00343             for( i = 0; i < ParamCount; i++ )
00344             {
00345                 const double d = ip[ i ] - cp[ i ];
00346                 s += d * d;
00347             }
00348 
00349             Radius += s * rc[ j ];
00350         }
00351 
00352         Radius = sqrt( Radius * s2 );
00353     }
